insert  into `risola`(`id_risola`,`tj_zagalovok`,`ru_zagalovok`,`en_zagalovok`,`tj_info`,`ru_info`,`en_info`) values (1,'Рисолати донишкада','Миссия','Mission','Мақсади асосии мо омода намудани аспирантони босаводе, ки нигоҳи мӯйшикофона ба ҳаёти ҷомеа доранд ва мушкиливу муаммоҳои ҷомеаро тавассути тадқиқот ҳал карда тавонанд. Ҷорӣ намудани идеяҳои нав, бунёди технологияҳои муосири ба ҳаёти рӯзмарра нигаронида шуда баҳри беҳбудии рӯзгори халқ.','Наша основная задача заключается в выпуске профессионалов аспирантов способных  мыслить критически, находить нестандартные решения, генерировать новые идеи, создать ресурсосберегающие технологии воблаго народа.','Our primary mission is to graduate competitive professionals who know how to think critically, to find non-standard solutions, to generate new knowledge and innovative ideas, to create a resource-saving technologies and who are able to learn long life.');

